import TasksApi from "@/api/TasksApi.js";
export default {
  data: () => {
    return {
      ListaDeTarefas: [],
      loading: false,
      filtro: "",
      exibir: {
        formGroups: false,
        listaGroups: true
      }
    };
  },
  methods: {
    listarTarefas() {
      TasksApi.getTasks(data => {
        this.ListaDeTarefas = data;
      });
    },

    vaiParaInicio() {
      this.$router.push("/");
    },

    vaiParaAdicionar() {
      this.$router.push("/newTask");
    },

    deletarTask(id) {
      TasksApi.delTasks(id, () => {
        this.listarTarefas();
      });
    },

    adicionarTask(task) {
      TasksApi.addTasks(task, () => {
        this.listarTarefas();
      });
    },

    atualizaTask(task) {
      console.log(task);
      TasksApi.updateTask(task, () => {
        this.listarTarefas();
      });
    }

  },

  created() {
    if (this.$route.params.task != undefined && this.$route.params.task.title != "" && this.$route.params.task.project != "") {
      this.adicionarTask(this.$route.params.task);
    } else {
      this.filtro = this.$route.params.filtro || "";
      this.listarTarefas();
    }

    if (this.$route.params.atualizaTarefa != undefined) {
      this.atualizaTask(this.$route.params.atualizaTarefa);
    }
  },

  computed: {
    filtragem() {
      return this.ListaDeTarefas.filter(titulo => titulo.project.includes(this.filtro));
    }

  }
};
