import axios from "axios";

export default {
  getTasks: (callback) => {
    axios.get("http://localhost:3001/api/tasks").then((response) => {
      callback(response.data);
    });
  },
  getGroups: (callback) => {
    axios.get("http://localhost:3001/groups").then((response) => {
      callback(response.data);
    });
  },
  createGroups: (data, callback) => {
    axios.post("http://localhost:3001/groups", data).then((response) => {
      callback(response.data);
    });
  },
  delGroups: (id, callback) => {
    axios.delete(`http://localhost:3001/groups/${id}`).then((response) => {
      callback(response.data);
    });
  },
  delTasks: (id, callback) => {
    axios.delete(`http://localhost:3001/api/tasks/${id}`).then((response) => {
      callback(response.data);
    });
  },
  addTasks: (task, callback) => {
    axios.post("http://localhost:3001/api/tasks/", task).then((response) => {
      callback(response.data);
    });
  },
};
