import TasksApi from "@/api/TasksApi.js";
export default {
  data: () => {
    return {
      newCourse: {
        name: ""
      },
      ListaDeTarefas: Array,
      listaDeCursos: Array,
      loading: false,
      exibir: {
        formGroups: false,
        listaGroups: true
      }
    };
  },
  methods: {
    vaiParaInicio() {
      this.$router.push("/");
    },

    listarTarefas(nomeTarefa) {
      this.$router.push({
        name: "TasksList",
        params: {
          filtro: nomeTarefa
        }
      });
    },

    cadastroGrupos() {
      this.$router.push("/groupsForm");
    },

    recebiSalvar(data) {
      TasksApi.createGroups(data, () => {
        this.newCourse.name = "";
        this.listarGrupos();
      });
    },

    excluirGrupo(tarefaId) {
      TasksApi.delGroups(tarefaId, () => {
        this.listarGrupos();
      });
    },

    listarGrupos() {
      TasksApi.getGroups(data => {
        this.listaDeCursos = data;
      });
    },

    salvarTarefas() {
      TasksApi.getTasks(data => {
        this.ListaDeTarefas = data;
      });
    },

    calculaQuantidade(nome) {
      return this.ListaDeTarefas.filter(titulo => titulo.project.includes(nome));
    }

  },

  created() {
    this.salvarTarefas();
    this.listarGrupos();

    if (this.$route.params.name != undefined) {
      if (this.$route.params.name.length > 0) {
        this.newCourse.name = this.$route.params.name;
        this.recebiSalvar(this.newCourse);
      }
    }
  }

};
