import TasksApi from "@/api/TasksApi.js";
export default {
  data: () => {
    return {
      summary: {},
      data: []
    };
  },
  methods: {
    vaiParaInicio() {
      this.$router.push("/");
    },

    listarTarefas(nomeTarefa) {
      this.$router.push({
        name: "TasksList",
        params: {
          filtro: nomeTarefa
        }
      });
    },

    cadastroGrupos() {
      this.$router.push("/groupsForm");
    },

    salvarTarefas() {
      TasksApi.getTasks(data => {
        this.ListaDeTarefas = data;
      });
    },

    calculaQuantidade(nome) {
      return this.ListaDeTarefas.filter(titulo => titulo.project.includes(nome));
    },

    getSummary() {
      TasksApi.summary().then(data => {
        console.log("carregando 2", data);
        this.summary = data;

        for (const [key, value] of Object.entries(data)) {
          const totalTasks = value.pending + value.working + value.done;
          let obj = {
            group: key,
            tasks: totalTasks,
            percent: 100 - value.pending / totalTasks * 100
          };
          console.log(obj);
          this.data.push(obj);
        }
      });
    }

  },

  created() {
    this.getSummary();
  }

};
