import Vue from "vue";
import VueRouter from "vue-router";
import HomeView from "@/views/HomeView.vue";
import LoginView from "@/views/LoginView.vue";
import ResumePainel from "@/views/ResumePainel.vue";
import NewUser from "@/views/NewUser.vue";
import TasksList from "@/views/TasksList.vue";
import GroupsForm from "@/views/GroupsForm.vue";
import addTask from "@/views/NewTask.vue";
import updateTask from "@/views/UpdateTask";

Vue.use(VueRouter);

const routes = [
  {
    path: "/",
    name: "home",
    component: HomeView,
  },
  {
    path: "/about",
    name: "about",
    // route level code-splitting
    // this generates a separate chunk (about.[hash].js) for this route
    // which is lazy-loaded when the route is visited.
    component: () =>
      import(/* webpackChunkName: "about" */ "../views/AboutView.vue"),
  },
  {
    path: "/login",
    name: "login",
    component: LoginView,
  },
  {
    path: "/resume",
    name: "resume",
    component: ResumePainel,
  },
  {
    path: "/register",
    name: "register",
    component: NewUser,
  },
  {
    path: "/",
    children: [
      {
        path: "/tasksList",
        name: "list",
        component: TasksList,
      },
      {
        path: "add",
        name: "addUpdate",
        component: addTask,
      },
      {
        path: ":id",
        name: "taskUpdate",
        component: updateTask,
      },
    ],
  },
  {
    path: "/groupsForm",
    name: "groupsForm",
    component: GroupsForm,
  },
];

const router = new VueRouter({
  mode: "history",
  base: process.env.BASE_URL,
  routes,
});

export default router;
