import api from "@/api";

export default {
  getTasks: (callback) => {
    api.get("http://localhost:3001/api/tasks").then((response) => {
      callback(response.data);
    });
  },
  getGroups: (callback) => {
    api.get("http://localhost:3001/api/project").then((response) => {
      callback(response.data);
    });
  },
  createGroups: (data, callback) => {
    api.post("http://localhost:3001/api/project", data).then((response) => {
      callback(response.data);
    });
  },
  delGroups: (id, callback) => {
    api.delete(`http://localhost:3001/api/project/${id}`).then((response) => {
      callback(response.data);
    });
  },
  delTasks: (id, callback) => {
    api.delete(`http://localhost:3001/api/tasks/${id}`).then((response) => {
      callback(response.data);
    });
  },
  addTasks: (task, callback) => {
    api.post("http://localhost:3001/api/tasks/", task).then((response) => {
      callback(response.data);
    });
  },
  updateTask: (task, callback) => {
    api
      .post(`http://localhost:3001/api/tasks/${task.id}`, task)
      .then((response) => {
        callback(response.data);
      });
  },
};
